import { Component, Input } from '@angular/core';
import { Pais } from '../types/Pais';

@Component({
  selector: 'app-generales',
  templateUrl: './generales.component.html',
  styleUrls: ['./generales.component.css']
})
export class GeneralesComponent {
  @Input() pais:Pais | undefined
  @Input("valor") valor:number=0


}
<div>
    <h1>valor:{{valor}}</h1>
    <h1>Nombre Comun: {{pais?.name?.common}}</h1>
    
    <h1>Nombre Oficial: {{pais?.name?.official}}</h1>
    <h1>Capital: {{pais?.capital}}</h1>
    <h1>Poblacion: {{pais?.population}} Habitantes</h1>

</div>
